(2, 'SD', 1, 'S/W Development', '', 'IT Asset Management System', '', '<div style="width:718px;">\r\n	<p>\r\n		<span style="font-size:16px;color: #087d8b;"><strong>• System definition</strong></span></p>\r\n	<p>\r\n		<span style="font-size: 14px;color: #444;">   A system launched with the aims of improving the productivity by effectively operated IT system. It manages all<br />\r\n		   information of IT assets (software, server, network equipment, PC, etc…) during purchasing, operating,<br />\r\n		   using, and discarding.</span></p>\r\n	<p>\r\n		<span style="font-size:14px;color: #444;">   → Remove all potential wastes through annalysing the expenditures of assets such as setting, consumable<br />\r\n		       equipment, reparing, etc.</span></p>\r\n	<p>\r\n		 </p>\r\n	<p>\r\n		<span style="font-size:16px;color: #087d8b;"><strong>• System structure</strong></span></p>\r\n	<p>\r\n		<img alt="" src="ckfinder/upload/userfiles/images/eng-S15.jpg" style="width: 717px;" /></p>\r\n</div>\r\n<p>\r\n	 </p>\r\n', '', '2015-10-16 00:00:00', 'Y', 'en', 'admin', '2015-10-16 00:00:00', 'admin', '2015-10-16 00:00:00'),
(3, 'SD', 2, 'S/W Development', '', 'Vehicle/ Personnel Entrance Management System', '', '<div style="width:718px;">\r\n	<p>\r\n		<span style="font-size: 16px;color:#087d8b;"><strong>• System definition</strong></span></p>\r\n	<p>\r\n		<span style="font-size:14px;color: #444;">   Vehicle / Personnel Entrance Management System control effectively the access of vehicle and person to<br />\r\n		   factory.</span></p>\r\n	<p>\r\n		<span style="font-size: 14px;color: #444;">   → Strengthen commute security management by computerization the management information of in/out staff<br />\r\n		       and semi import/export product.</span></p>\r\n	<p>\r\n		<span style="font-size: 14px;color: #444;">   → Save and share the information of entrance vehicle, material supplier and consumer.</span></p>\r\n	<p>\r\n		 </p>\r\n	<p>\r\n		<span style="font-size: 16px;color:#087d8b;"><strong>• System structure</strong></span></p>\r\n	<p>\r\n		<span style="font-size:16px;"><img alt="" src="ckfinder/upload/userfiles/images/eng-S15b.jpg" style="width: 717px; " /></span></p>\r\n</div>\r\n<p>\r\n	 </p>\r\n', '', '2015-10-16 00:00:00', 'Y', 'en', 'admin', '2015-10-16 00:00:00', 'admin', '2015-10-16 00:00:00'),
(4, 'SD', 3, 'S/W Development', '', 'Vehicle Weighing system', '', '<div style="width:718px;">\r\n	<p>\r\n		<span style="font-size: 16px;color:#087d8b;"><strong>• System definition</strong></span></p>\r\n	<p>\r\n		<span style="font-size: 14px;color: #444;">   Manage exactly the history of vehicle and the weight </span><span style="color: rgb(68, 68, 68); font-size: 14px;">information </span><span style="color: rgb(68, 68, 68); font-size: 14px;">of input/output goods on entrance vehicle at<br />\r\n		   factory.</span></p>\r\n	<p>\r\n		<span style="font-size:14px;"><span style="color: rgb(68, 68, 68);">   → Manage the input/ output weight of vehicle: manage the output statistics history of monthly / yearly,<br />\r\n		       quantification for output quantity.</span></span></p>\r\n	<p>\r\n		 </p>\r\n	<p>\r\n		<span style="font-size: 16px;color:#087d8b;"><strong>• System structure</strong></span></p>\r\n	<p>\r\n		<span style="font-size:16px;"><img alt="" src="ckfinder/upload/userfiles/images/eng-S17.jpg" style="width: 717px;" /></span></p>\r\n</div>\r\n<p>\r\n	 </p>\r\n', '', '2015-10-16 00:00:00', 'Y', 'en', 'admin', '2015-10-16 00:00:00', 'admin', '2015-10-16 00:00:00'),
(5, 'SD', 4, 'S/W Development', '', 'Equipment Management System', '', '<div style="width:718px;">\r\n	<p>\r\n		<span style="font-size: 16px;color:#087d8b;"><strong>• System definition</strong></span></p>\r\n	<p>\r\n		<span style="font-size: 14px;color: #444;">   Manage information of testing progress, repair, equipment distribution from warehousing to stop using for<br />\r\n		   optimizing the use of product features.</span></p>\r\n	<p>\r\n		<span style="font-size: 14px; color: #444;">   → The system configuration information of the device, inspection standard, repair, enhanced operation of<br />\r\n		       devices and device management standards.</span></p>\r\n	<p>\r\n		 </p>\r\n	<p>\r\n		<span style="font-size: 16px;color:#087d8b;"><strong>• System structure</strong></span></p>\r\n	<p>\r\n		<span style="font-size:16px;"><img alt="" src="ckfinder/upload/userfiles/images/eng-S18.jpg" style="width: 717px;" /></span></p>\r\n</div>\r\n<p>\r\n	 </p>\r\n', '', '2015-10-16 00:00:00', 'Y', 'en', 'admin', '2015-10-16 00:00:00', 'admin', '2015-10-16 00:00:00'),
(6, 'SD', 5, 'S/W Development', '', 'BAPS system', '', '<div style="width:718px;">\r\n	<p>\r\n		<span style="font-size: 16px;color:#087d8b;"><strong>• System Definition</strong></span></p>\r\n	<p>\r\n		<span style="font-size: 14px;color: #444;">   BAPS(Business Advanced Platform Service) is a comprehensive information portal solution to support<br />\r\n		   production, business and innovation of enterprises.</span></p>\r\n	<p>\r\n		 </p>\r\n	<p>\r\n		<span style="font-size: 16px;color:#087d8b;"><strong>• System structure</strong></span></p>\r\n	<p>\r\n		<img alt="" src="ckfinder/upload/userfiles/images/baps_en.png" style="width: 718px;" /></p>\r\n	<p>\r\n		 </p>\r\n</div>\r\n<p>\r\n	 </p>\r\n', '', '2015-10-16 00:00:00', 'Y', 'en', 'admin', '2015-10-16 00:00:00', 'admin', '2015-10-16 00:00:00'),
(8, 'AT', 1, 'News & Notice', 'Posco ICT Vietnam', 'Lắp ráp và cập nhật máy vi tính cho phòng máy tính ở trường trung học Hùng Vương', 'Với mong muốn được đóng góp vào sự phát triển giáo dục của địa phương. Posco Ict - Vietnam đã chủ động liên hệ với các đối tác bạn và chính quyền địa phương lên phương án xây dựng 1 phòng học vi tính cho trường trung học Hùng Vương', '<p>\r\n	<span style="font-size:14px;color:#444;">Nhằm góp phần vào việc xây dựng và phát triển sự nghiệp giáo dục tại địa phương, trong 2 ngày 23, 24/5/2014 công ty POSCO ICT Việt Nam, Habitat và POSCO Việt Nam cùng phối hợp với lãnh đạo UBND xã Tân Hòa đã tổ chức một buổi hoạt động tình nguyện đó là tặng phòng máy vi tính, lắp ráp và cập nhật máy vi tính tại trường trung học Hùng Vương, Phước Tân, Tân Hòa, huyện Tân Thành, Bà Rịa Vũng Tàu.</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/LDMT20141113_1.jpg" style="width: 718px; height: 532px;" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size:14px;color:#444;"><strong>Phòng máy vi tính tại trường Trung học Hùng Vương</strong></span></p>\r\n<p>\r\n	<span style="font-size:14px;color:#444;">Tại hoạt động này POSCO ICT Việt Nam đã sử dụng nguồn nhân lực cũng như năng lực chuyên môn IT sẵn có để lắp ráp và cập nhật máy tính tại phòng máy vi tính cho đối tượng là học sinh của trường trung học Hùng Vương.</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/LDMT20141113_2.jpg" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size:14px;color:#444;"><strong>Ông Park Yong Nam - phó giám đốc công ty POSCO ICT Việt Nam đến giám sát và các nhân viên của công ty đến lắp ráp và cập nhật máy tính.</strong></span></p>\r\n<p>\r\n	<span style="font-size:14px;color:#444;">Hoạt động này với mục đích hỗ trợ cho các em học sinh có cơ hội làm quen và luyện tập với máy vi tính và nâng cao trình độ về IT.</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/LDMT20141113_3.jpg" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size:14px;color:#444;"><strong>Các nhân viên đang lắp ráp và cập nhật máy tính</strong></span></p>\r\n<p>\r\n	<span style="font-size:14px;color:#444;">Khi đó công ty còn nhận được giấy tri ân của trường trung học cơ sở Hùng Vương xã Tân Hòa.</span></p>\r\n<p>\r\n	<span style="font-size:14px;color:#444;">Cũng tại nơi đây, sau khi cài đặt lắp ráp ông Kim Kwang Seok- tổng giám đốc công ty Posco ICT Việt Nam và ông Park Yong Nam- phó giám đốc công ty Posco ICT Việt Nam cùng với các nhân viên của công ty sẽ quản lý các máy vi tính ở đây.</span></p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/LDMT20141113_4.jpg" /></p>\r\n<p>\r\n	<span style="font-size:14px;color:#444;"><strong>Các học sinh tại trường tham gia một cách vui vẻ trong ngày khai mạc phòng máy</strong></span></p>\r\n<p>\r\n	<span style="font-size:14px;color:#444;">Sau hoạt động này công ty còn cử nhân viên có chuyên môn hàng tháng đến hướng dẫn cũng như chỉ dạy cho các đối tượng muốn biết thêm cũng như nâng cao trình độ về IT như học sinh, công nhân viên chức, người dân địa phương…</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/LDMT20141113_5.jpg" /></p>\r\n<p>\r\n	<span style="font-size:14px;color:#444;"><strong>Giấy tri ân của Trường trung học Hùng Vương</strong></span></p>\r\n<p>\r\n	<span style="font-size:14px;color:#444;">Trong tương lai, Posco ICT Việt Nam sẽ còn tổ chức đa dạng các hoạt động tình nguyện như thế này.</span></p>\r\n<p>\r\n	 </p>\r\n', 'ckfinder/upload/userfiles/images/LDMT20141113_4.jpg', '2014-11-20 08:05:01', 'Y', 'vn', 'admin', '2015-10-16 00:00:00', 'admin', '2014-11-20 08:05:01'),
(9, 'AT', 2, 'News & Notice', 'Posco ICT Vietnam', 'Tổ chức lớp đào tạo vi tính cho học sinh, cư dân, công nhân viên chức... tại xã Tân Hòa, huyện Tân Thành', 'Với mục đích phổ cập kiến thức về CNTT cho các em học sinh, công nhân viên chức và cư dân tại xã Tân Hòa, huyện Tân Thành. Công ty Posco ICT- Việt Nam cùng với công ty Posco Việt Nam đã tổ chức các lớp đào tạo vi tính trong 3 ngày (12-&gt; 14/10/2013) tại trung tâm học tập cộng đồng xã Tân Hòa, huyện Tân Thành.', '<p>\r\n	<span style="font-size:14px; color:#444;">Với mục đích phổ cập kiến thức về CNTT cho các em học sinh, công nhân viên chức và cư dân tại xã Tân Hòa, huyện Tân Thành. Công ty POSCO ICT Việt Nam cùng với công ty POSCO Việt Nam đã tổ chức các lớp đào tạo vi tính trong 3 ngày (12 -> 14/10/2013) tại trung tâm học tập cộng đồng xã Tân Hòa, huyện Tân Thành.</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/DVT_1.JPG" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/DVT_2.JPG" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size:14px; color:#444;">Lớp đào tạo vi tính này được mở ra nhằm giúp mọi người tại địa phương được rèn luyện và trao dồi kỹ năng về CNTT và hiểu rõ hơn về các kiến thức căn bản cũng như chuyên sâu hơn về CNTT như: soạn thảo, định dạng văn bản, lập bảng tính, tạo silde trình chiếu, photoshop thông qua việc sử dụng thành thạo các phần mềm như Word, Excel, Powerpoint…</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/DVT_3.JPG" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size:14px; color:#444;">Tại lớp đào tạo này không chỉ có các em học sinh của các trường tham gia mà còn có công nhân viên chức và người dân tại xã Tân Hòa đến tham gia. Số lượng học sinh đăng ký tham gia lên đến 45 ~ 50 người.</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/DVT_4.JPG" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/DVT_5.JPG" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size:14px; color:#444;">Giáo viên đảm trách các lớp đào tạo này là những nhân viên vui tính, dễ thương và đầy kinh nghiệm về CNTT của POSCO ICT Việt Nam. Ngoài việc dạy học giáo viên thỉnh thoảng còn tổ chức nhiều buổi party bánh kẹo nho nhỏ để giúp cho các học sinh tham gia đỡ nhàm chán với lớp học mà còn tạo cho mối quan hệ giữa thầy và trò thêm gắn kết.</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size:14px; color:#444;">Khóa học đào tạo này đã luôn được duy trì mỗi tháng 1 lần nhưng từ tháng 10/2014 vì nhu cầu của các học sinh nên đã gia tăng buổi học thành 2 lần 1 tháng.</span></p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/DVT_6.JPG" /></p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/DVT_7.JPG" /></p>\r\n<p>\r\n	 </p>\r\n', 'ckfinder/upload/userfiles/images/DVT_7.JPG', '2014-11-20 08:05:01', 'Y', 'vn', 'admin', '2015-10-16 00:00:00', 'admin', '2014-11-20 08:05:01'),
(10, 'AT', 3, 'News & Notice', 'Posco ICT Vietnam', 'Tổ chức các chuyến đi thực tế cho nhân viên', 'Thông qua các chuyến đi thực tế, nhân viên POSCO ICT Việt Nam thu được những kiến thức thực tiễn về quy trình  sản xuất và hoạt động kinh doanh, từ đó, đặt ra kế hoạch hỗ trợ  hiệu quả cho công ty khách hàng.', '<p>\r\n	<span style="font-size:14px; color:#444;">Thông qua các chuyến đi thực tế, nhân viên POSCO ICT Việt Nam thu được những kiến thức thực tiễn về quy trình  sản xuất và hoạt động kinh doanh, từ đó, đặt ra kế hoạch hỗ trợ hiệu quả cho công ty khách hàng.</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/FIELD_1.JPG" style="width: 718px; height: 538px;" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/FIELD_2.JPG" style="width: 718px; height: 538px;" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<strong><span style="font-size:14px; color:#444;">POSCO Việt Nam ( KCN Phú Mỹ 2, BRVT)</span></strong></p>\r\n<p>\r\n	<span style="font-size:14px; color:#444;">Thăm nhà máy POSCO Việt Nam, được nghe giới thiệu về công ty và tham quan dây chuyền sản xuất thép quy mô của POSCO Việt Nam.</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/FIELD_3.JPG" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/FIELD_4.JPG" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/FIELD_5.JPG" /></p>\r\n<p>\r\n	<span style="font-size:14px; color:#444;"><strong>POSCO VST ( KCN Nhơn Trạch  1, Đồng Nai)</strong></span></p>\r\n<p>\r\n	<span style="font-size:14px; color:#444;">Tham quan toàn dây chuyền thép không gỉ, gặp gỡ và trao đổi, tìm hiểu nghiệp vụ tại từng Phòng quản lý quy trình của nhà máy POSCO VST.</span></p>\r\n<p>\r\n	 </p>\r\n', 'ckfinder/upload/userfiles/images/FIELD_2.JPG', '2014-11-25 10:15:10', 'Y', 'vn', 'admin', '2015-10-16 00:00:00', 'admin', '2014-11-25 10:15:10'),
(11, 'NN', 4, 'News & Notice', 'Posco ICT Vietnam', 'Thành lập POSCO ICT Việt Nam', 'Sau 3 năm hoạt động tại Việt Nam dưới hình thức Văn phòng Đại diện, ngày 01/10/2013,  Văn phòng Đại diện POSCO ICT tại Tp.HCM chính thức  trở thành chi nhánh đầu tiên của POSCO ICT tại Việt Nam với tên gọi pháp nhân Công ty TNHH POSCO ICT Việt Nam.', '<p>\r\n	<span style="font-size:14px;"><span style="color:#444;">Sau 3 năm hoạt động tại Việt Nam dưới hình thức Văn phòng Đại diện, ngày 01/10/2013, Văn phòng Đại diện POSCO ICT tại Tp.HCM chính thức  trở thành chi nhánh đầu tiên của POSCO ICT tại Việt Nam với tên gọi pháp nhân Công ty TNHH POSCO ICT Việt Nam.</span></span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/TLCT_1.JPG" style="width: 718px; height: 478px;" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/TLCT_2.JPG" style="width: 718px; height: 478px;" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/TLCT_3.JPG" style="width: 718px; height: 478px;" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="color:#444;"><span style="font-size:14px;">Ngày 01/10/2013, tại trụ sở chính công ty ( Phòng 604A, Diamond Plaza, 34 Lê Duẩn, Quận 1, Tp.HCM) đã diễn ra buổi Lễ khánh thành công ty POSCO ICT VIỆT NAM. Buổi lễ có sự hiện diện của nhiều đại diện các công ty đối tác, các tổ chức, Hiệp hội doanh nghiệp Hàn Quốc tại Việt Nam, cùng đại diện POSCO ICT từ công ty mẹ tại Hàn Quốc đến dự cắt băng khánh thành.</span></span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="color:#444;"><span style="font-size:14px;">Đặc biệt, trong bải phát biểu chúc mừng của Ngài Tổng Lãnh Sự quán Hàn Quốc tại TP. Hồ Chí Minh, bên cạnh lời chúc mừng và lời chúc tốt đẹp cho sự phát triển lâu dài của công ty, còn có lời hứa “Lãnh Sự quán Hàn Quốc tại Việt Nam sẽ hỗ trợ hết mình cho sự bền vững và hoạt động hợp pháp của POSCO ICT Việt Nam tại Việt Nam”.</span></span></p>\r\n<p>\r\n	 </p>\r\n', 'ckfinder/upload/userfiles/images/TLCT_3.JPG', '2014-11-25 10:15:10', 'Y', 'vn', 'admin', '2015-10-16 00:00:00', 'admin', '2014-11-25 10:15:10'),
(12, 'NN', 5, 'News & Notice', 'Posco ICT Vietnam', 'Sinh nhật POSCO ICT Việt Nam vừa tròn một năm tuổi', 'Ngày 01/10/2014, công ty POSCO ICT Việt Nam tròn 1 năm tuổi.', '<p>\r\n	<span style="color:#444;"><span style="font-size: 14px;">Ngày 01/10/2014: Công ty POSCO ICT Việt Nam tròn 1 năm tuổi.</span></span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/1NTL_7.JPG" style="width: 718px; height: 538px;" /></p>\r\n<p>\r\n	<span style="font-size: 14px; color: rgb(68, 68, 68);">Bài phát biểu của Ngài Tổng Giám Đốc Kim Kwang Suk báo cáo tình hình kinh doanh công ty sau 1 năm thành lập và chiến lược kinh doanh trong năm sắp tới.</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/1NTL_5.JPG" style="width: 718px; height: 517px;" /></p>\r\n<p>\r\n	<span style="font-size: 14px; color: rgb(68, 68, 68);">Lễ cắt bánh mừng Công ty tròn 1 tuổi. Bánh “sinh nhật” được cắt bởi Tổng Giám Đốc và đại diện một số nhân viên xuất sắc đến từ các bộ phận.</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/1NTL_6.JPG" /></p>\r\n<p>\r\n	<span style="color:#444;"><span style="font-size:14px;">Chụp hình lưu niệm toàn thể nhân viên tại buổi lễ Kỷ niệm 01 năm thành lập công ty.</span></span></p>\r\n<p>\r\n	 </p>\r\n', 'ckfinder/upload/userfiles/images/1NTL_6.JPG', '2014-11-25 10:15:10', 'Y', 'vn', 'admin', '2015-10-16 00:00:00', 'admin', '2014-11-25 10:15:10'),
(13, 'AT', 4, 'News & Notice', 'Posco ICT Vietnam', 'Ngày hoạt động xã hội', 'Văn hóa doanh nghiệp của POSCO ICT Việt Nam không chỉ có các hoạt động kinh doanh mà còn chú trọng cả các hoạt động tình nguyện mang tính nhân văn, đóng góp vào sự phát triển xã hội của Việt Nam.', '<p>\r\n	<span style="font-size:14px; color:#444;">Văn hóa doanh nghiệp của POSCO ICT Việt Nam không chỉ có các hoạt động kinh doanh mà còn chú trọng cả các hoạt động tình nguyện mang tính nhân văn, đóng góp vào sự phát triển xã hội của Việt Nam.</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size:14px; color:#444;"><strong>Hoạt động phát cơm từ thiện tại KCN Tân Bình, Tp. HCM</strong></span></p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/PCTS_1.JPG" style="width: 718px; height: 536px;" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size:14px; color:#444;">Phát cơm từ thiện định kỳ được thực hiện 2 lần/ tháng bởi cả nhân viên người Hàn Quốc và Việt Nam.</span></p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/PCTS_2.JPG" style="width: 718px; height: 477px;" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/PCTS_3.JPG" style="width: 718px; height: 477px;" /></p>\r\n<p>\r\n	<span style="font-size:14px; color:#444;">Những gương mặt vui cười đón nhận khay cơm chính là hạnh phúc của nhân viên POSCO ICT VIỆT NAM khi tham gia hoạt động  ý nghĩa này.</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size:14px; color:#444;"><strong>Tặng sách cho các em nhỏ tại KCN Tân Bình, Tp. HCM</strong></span></p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/PCTS_4.JPG" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/PCTS_5.JPG" style="width: 718px; height: 404px;" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/PCTS_6.JPG" style="width: 718px; height: 404px;" /></p>\r\n<p>\r\n	 </p>\r\n', 'ckfinder/upload/userfiles/images/PCTS_6.JPG', '2014-11-25 10:15:10', 'Y', 'vn', 'admin', '2015-10-16 00:00:00', 'admin', '2014-11-25 10:15:10'),
(14, 'OV', 1, 'Overview', '우리 모두를 위해 더 나은 미래를 만들어 갑니다.    ', '', '', '<p style="text-align: left;">\r\n	<span style="font-size:14px;"><span style="color:#444;">고객의 경쟁력 향상을 위해</span> <span style="color:#00457c;"><strong>IT와 Engineering 전반에 걸친 폭넓고 풍부한 핵심역량을 기반</strong></span><span style="color:#444;">으로 차별화된 ICT 통합 솔루션을 제공합니다.</span></span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size:14px;"><img alt="" src="ckfinder/upload/userfiles/images/kr-S5.jpg" style="width: 718px;" /></span></p>\r\n<p>\r\n	 </p>\r\n<p style="text-align: left;">\r\n	<span style="font-size:14px;"><span style="color:#444;">포스코ICT는</span> <span style="color:#00457c;"><strong>플랜트, 물류, 환경, 에너지, 공항 등 다양한 산업분야에서 최상의 솔루션</strong></span><span style="color:#444;">으로서 고객의 요구에 최선을 다하고 있습니다.</span></span></p>\r\n<p style="text-align: left;">\r\n	<span style="font-size:14px;"><span style="color:#444;">포스코ICT는 고객에 대한 가장 확실한 서비스는 ‘품질 경쟁력’이라는 믿음 아래</span> <span style="color:#00457c;"><strong>품질, 환경, 안전 관리에 대한 국제인증을 획득</strong></span><span style="color:#444;">하여 운영하고 있습니다. </span></span></p>\r\n<p style="text-align: left;">\r\n	<span style="font-size:14px;"><span style="color:#444;">국내외 유수 기업 ∙ 학계 ∙ 연구소와 상호 협력을 통하여</span><span style="color:#00457c;"><strong> 새로운 기술의 우위를 선점하고, 창의적 아이디어를 바탕으로 새로운 비즈니스를 창출</strong></span><span style="color:#444;">하기 위해 노력합니다.</span></span></p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="color:#444;">• Finding Biz Chance</span></span></p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="color:#444;">• Open Innovation을 통한 솔루션 확보</span></span></p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="color:#444;">• New Biz. Incubation</span></span></p>\r\n<p>\r\n	 </p>\r\n<p style="text-align: left;">\r\n	<span style="font-size:14px;"><span style="color:#444;">파트너사, 지역사회를 비롯한 이해관계자들과 신뢰를 기반한 </span><span style="color:#00457c;"><strong>CSR (Corporate Social Responsibility) 실천을 통해 지속 가능한 기업으로 성장</strong></span>하고 있습니다.</span></p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="color: rgb(68, 68, 68);">• 글로벌 수준의 기업윤리 실천</span></span></p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="color:#444;">• 파트너사와 신뢰를 기반으로 상생협력</span></span></p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="color:#444;">• 감사나눔을 통한 신뢰와 긍정의 기업문화 실현 </span></span></p>\r\n<p>\r\n	 </p>\r\n<p style="text-align: left;">\r\n	<span style="font-size:14px;"><span style="color:#444;">포스코ICT는</span> <span style="color:#00457c;"><strong>플랜트, 물류, 환경, 에너지, 공항 등 다양한 산업분야에서 최상의 솔루션</strong></span><span style="color:#444;">으로서 고객의 요구에 최선을 다하고 있습니다.</span></span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size:14px;"><img alt="" src="ckfinder/upload/userfiles/images/overview1_kr.png" style="width: 718px; height: 457px;" /></span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	 </p>\r\n', '', '2015-10-23 10:15:10', 'Y', 'kr', 'admin', '2015-10-23 10:15:10', 'admin', '2015-10-23 10:15:10'),
(15, 'OV', 1, 'Overview', 'We create the tomorrow            ', '', '', '<p>\r\n	<span style="font-size:14px;"><span style="color:#444;">For customer competition capacity increasing, we provide differentiated ICT integration solutions base on a</span> <span style="color:#00457c;"><strong>wide and rich experience range of IT and Engineering.</strong></span></span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="color:#0099ff;"><strong><img alt="" src="ckfinder/upload/userfiles/images/eng-S5.jpg" style="width: 718px;" /></strong></span></span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="color:#444;">POSCO ICT believe quality and safety are fundamental to our business that we have been</span> <span style="color:#00457c;"><strong>internationally certified in the areas of quality, environment, and safety management. </strong></span></span></p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="color:#444;">By the cooperate with international and domestic corporation, institute, colleague, POSCO ICT makes efforts to search and develop Innovative Opportunity for a new growth engine that provides new value to customers based on</span> <span style="color:#00457c;"><strong>Imagining, Creativity, and Transformation.</strong></span></span></p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="color: rgb(68, 68, 68);">• Finding Biz Chance</span></span></p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="color: rgb(68, 68, 68);">• Develop the Solution through Open Innovation</span></span></p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="color: rgb(68, 68, 68);">• New Biz. Incubation</span></span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="color:#444;">POSCO ICT continues to work and grow on sharing values through to</span><span style="color:#00457c;"><strong> CSR (Corporate Social Responsibility) implementation base on partner and community.</strong></span></span></p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="color: rgb(68, 68, 68);">• Global business ethics built-up</span></span></p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="color: rgb(68, 68, 68);">• Win – Win cooperation base on partner truth</span></span></p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="color: rgb(68, 68, 68);">• Built the gratitude and the business culture  with a thankful mind and the happiness of sharing</span></span></p>\r\n<p>\r\n	 </p>\r\n<div>\r\n	<p>\r\n		<span style="font-size:14px;"><span style="color:#444;">We provide the best solutions and service at the most competitive price </span><strong style="color: #00457c; font-size: 14px;">in the field of plants, logistics, environment, energy, transport, etc.</strong></span></p>\r\n	<p>\r\n		 </p>\r\n	<p>\r\n		<span style="font-size:14px;"><span style="color:#0099ff;"><strong><img alt="" src="ckfinder/upload/userfiles/images/eng-S7.png" style="width: 718px;" /></strong></span></span></p>\r\n</div>\r\n<p>\r\n	 </p>\r\n', '', '2014-11-25 10:15:10', 'Y', 'en', 'admin', '2015-10-16 00:00:00', 'admin', '2014-11-25 10:15:10'),
(16, 'RC', 0, 'Recruitment', 'Welcome to attend to our POSCO ICT Vietnam Family.', 'Welcome to attend to our POSCO ICT Vietnam Family.', '', '<p>\r\n	<span style="color:#00457c; font-size: 18px;"><strong>We want to recruit and retain talented people who want to lead the future of POSCO ICT Vietnam.</strong></span></p>\r\n<p style="padding-top:10px;">\r\n	<strong><span style="color:#087d8b; font-size: 16px;">Design your future with POSCO ICT Vietnam.</span></strong></p>\r\n<p style="padding-top:10px;">\r\n	<span style="color:#444; font-size: 14px;">If you are interested, you can contact our HR team for the opportunities.</span></p>\r\n<p style="padding-top:10px;">\r\n	<span class="fa fa-phone" style="color: rgb(68, 68, 68); font-size: 16px;">  </span><span style="color:#444; font-size: 14px;">Phone: (84) 8 3824 0739</span></p>\r\n<p style="padding-top:10px;">\r\n	<span class="fa fa-envelope" style="color: rgb(68, 68, 68); font-size: 16px;">  </span><span style="color:#444; font-size: 14px;">Email: hr@posco.net</span></p>\r\n<p style="padding-top:10px;">\r\n	<span class="fa fa-globe" style="color: rgb(68, 68, 68); font-size: 18px;"> </span><span style="color:#444; font-size: 14px;"> Website: </span> <u><a href="http://poscoict-vietnam.com/recruit.do" style="color:#008080; font-size: 14px;">www.poscoict-vietnam.com</a></u></p>\r\n', '', '2014-11-25 10:15:10', 'y', 'en', 'admin', '2015-10-16 00:00:00', 'admin', '2014-11-25 10:15:10'),
(17, 'AT', 1, 'News & Notice', 'Posco ICT Vietnam', 'Installation and update computers for computer labs in Hung Vuong school', 'With a desire to contribute to the development of local education, POSCO ICT Vietnam took the initiative to contact your partners and local government to build alternative a computer classroom for Hung Vuong school', '<p>\r\n	<span style="color: #444; font-size: 14px;">To contribute to the formulation and development of education at local, in 2 days of May 23, 24 2014 POSCO ICT Vietnam company, POSCO Vietnam, habitat and in cooperation with the CPC Tan Hoa leaders have organized a volunteer activity that is donated computer lab, assemble and update computers at school Hung Vuong, Phuoc Tan, Tan Hoa commune, Tan Thanh District, Ba Ria Vung Tau.</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/LDMT20141113_1.jpg" style="width: 718px; height: 532px;" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="color: #444; font-size: 14px;"><strong>Computer room at Hung Vuong secondary school</strong></span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="color: #444; font-size: 14px;">POSCO ICT activities in Vietnam for using human resources and technical IT capacity available to assemble computer and update the computer room for high school students&#39; Hung Vuong school.</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/LDMT20141113_2.jpg" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="color: #444; font-size: 14px;"><strong>Mr. Park Yong Nam - Deputy Manager of POSCO ICT Vietnam to monitor and corporate employees to assemble and update the computer.</strong></span></p>\r\n<p>\r\n	<span style="color: #444; font-size: 14px;">This activity aims to support students with the opportunity to familiarize and practice with computers and improve the level of IT.</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/LDMT20141113_3.jpg" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="color: #444; font-size: 14px;"><strong>The staff is assembling and updating computers.</strong></span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="color: #444; font-size: 14px;">Meanwhile the company also received a certificate of gratitude to Hung Vuong secondary school of Tan Hoa commune.</span></p>\r\n<p>\r\n	<span style="color: #444; font-size: 14px;">After installing the assembly, Mr. Kim Kwang Seok, General Manager of POSCO ICT Vietnam company and Mr. Park Yong Nam, Deputy Manager and all staff will manage the computers here.</span></p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/LDMT20141113_4.jpg" /></p>\r\n<p>\r\n	<span style="color: #444; font-size: 14px;"><strong>The students at the school participate in a fun way on the opening day engine room.</strong></span></p>\r\n<p>\r\n	<span style="color: #444; font-size: 14px;">After this operation the company has staff with expertise to guide every month, as well as teach people who wanted to know more and improve the level of IT as students, workers and local residents,...</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/LDMT20141113_5.jpg" /></p>\r\n<p>\r\n	<span style="color: #444; font-size: 14px;"><strong>Certificate of gratitude of Hung Vuong secondary school</strong></span></p>\r\n<p>\r\n	<span style="color: #444; font-size: 14px;">In the future, POSCO ICT Vietnam will organize a variety of volunteer activities like this.</span></p>\r\n<p>\r\n	 </p>\r\n', 'ckfinder/upload/userfiles/images/LDMT20141113_4.jpg', '2014-11-20 08:05:01', 'Y', 'en', 'admin', '2015-10-16 00:00:00', 'admin', '2014-11-20 08:05:01'),
(18, 'AT', 2, 'News & Notice', 'Posco ICT Vietnam', '학생, 주민, 노동자와 직원을위한 컴퓨터 교육 과정의 조직 ... 탄 호아 코뮌, 탄 탄 지구', '학생, 노동자와 직원과 탄 호아 코뮌 주민 인쇄, 탄 탄 지역에 대한 지식을 대중화의 목표로. 탄 호아 코뮌 지역 사회 학습 센터 - 포스코 ICT 회사와 포스코 베트남 베트남 조직 된 3 일 (2013년 10월 14일 12)의 컴퓨터 교육 과정이있다.', '<div>\r\n	<span>• 탄 호아, 탄 탄 지구 학생, 노동자와 직원 및 주민을위한 정보 통신 기술에 대한 지식을 대중화의 목표로. 포스코 베트남과 포스코 베트남 ICT- 회사가 지역 사회 학습 센터 탄 호아 코뮌 3 일 (12> 2013년 10월 14일)에서 컴퓨터 교육 과정을 구성하고있다, 탄 탄 지구.</span></div>\r\n<div>\r\n	 </div>\r\n<div>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/DVT_1.JPG" /></div>\r\n<div>\r\n	 </div>\r\n<div>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/DVT_2.JPG" /></div>\r\n<div>\r\n	 </div>\r\n<div>\r\n	<span>• Lớp đào tạo vi tính này được mở ra nhằm giúp mọi người tại địa phương được rèn luyện và trao dồi kỹ năng về CNTT và hiểu rõ hơn về các kiến thức căn bản cũng như chuyên sâu hơn về CNTT như: soạn thảo, định dạng văn bản, lập bảng tính, tạo silde trình chiếu, photoshop thông qua việc sử dụng thành thạo các phần mềm như Word, Excel, Powerpoint…</span></div>\r\n<div>\r\n	 </div>\r\n<div>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/DVT_3.JPG" /></div>\r\n<div>\r\n	 </div>\r\n<div>\r\n	<span>• Tại lớp đào tạo này không chỉ có các em học sinh của các trường tham gia mà còn có công nhân viên chức và người dân tại xã Tân Hòa đến tham gia. Số lượng học sinh đăng ký tham gia lên đến 45~50 người.</span></div>\r\n<div>\r\n	 </div>\r\n<div>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/DVT_4.JPG" /></div>\r\n<div>\r\n	 </div>\r\n<div>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/DVT_5.JPG" /></div>\r\n<div>\r\n	 </div>\r\n<div>\r\n	<span>• </span>Giáo viên đảm trách các lớp đào tạo này là những nhân viên vui tính, dễ thương và đầy kinh nghiệm về CNTT của Posco ICT – Việt Nam. Ngoài việc dạy học giáo viên thỉnh thoảng còn tổ chức nhiều buổi party bánh kẹo nho nhỏ để giúp cho các học sinh tham gia đỡ nhàm chán với lớp học mà còn tạo cho mối quan hệ giữa thầy và trò thêm gắn kết.</div>\r\n<div>\r\n	<p>\r\n		<span>• </span>Khóa học đào tạo này đã luôn được duy trì mỗi tháng 1 lần nhưng từ tháng 10/2014 vì nhu cầu của các học sinh nên đã gia tăng buổi học thành 2 lần 1 tháng.</p>\r\n</div>\r\n<div style="text-align: justify;">\r\n	<p>\r\n		<img alt="" src="ckfinder/upload/userfiles/images/DVT_6.JPG" style="" /></p>\r\n	<p>\r\n		<img alt="" src="ckfinder/upload/userfiles/images/DVT_7.JPG" /></p>\r\n</div>\r\n', 'ckfinder/upload/userfiles/images/DVT_7.JPG', '2014-11-20 08:05:01', 'Y', 'kr', 'admin', '2015-10-16 00:00:00', 'admin', '2014-11-20 08:05:01'),
(19, 'AT', 3, 'News & Notice', 'Posco ICT Vietnam', 'Organizing field trips for employees', 'Through field trips, POSCO ICT Vietnam staff obtain practical knowledge of production processes and operations, thereby, carried out plans to support effectiveness for client companies.', '<p>\r\n	<span style="color: #444; font-size: 14px;">Through field trips, POSCO ICT Vietnam staff obtain practical knowledge of production processes and operations, thereby, carried out plans to support effectiveness for client companies.</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/FIELD_1.JPG" style="width: 718px; height: 538px;" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/FIELD_2.JPG" style="width: 718px; height: 538px;" /></p>\r\n<p>\r\n	<strong style="color: rgb(68, 68, 68); font-size: 14px;">POSCO Viietnam (Phu My Industrial Zone 2, BRVT)</strong></p>\r\n<p>\r\n	<span style="color: #444; font-size: 14px;">Visiting POSCO Vietnam factory was introduced to the company and the production line of steel scale of POSCO Vietnam.</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/FIELD_3.JPG" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/FIELD_4.JPG" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/FIELD_5.JPG" /></p>\r\n<p>\r\n	<span style="color: #444; font-size: 14px;"><strong>POSCO VST (Nhon Trach Industrial Zone 1, Đong Nai)</strong></span></p>\r\n<p>\r\n	<span style="color: #444; font-size: 14px;">Visiting full line of stainless steel, to meet and exchange and learn business process management of POSCO VST factory.</span></p>\r\n<p>\r\n	 </p>\r\n', 'ckfinder/upload/userfiles/images/FIELD_2.JPG', '2014-11-25 10:15:10', 'Y', 'en', 'admin', '2015-10-16 00:00:00', 'admin', '2014-11-25 10:15:10'),
(20, 'NN', 4, 'News & Notice', 'Posco ICT Vietnam', 'Establishment of POSCO ICT Vietnam', 'After 3 years of operation in Vietnam in the form of a Representative Office, dated 10.01.2013, POSCO ICT representative office in HCM City officially became the first branch of POSCO ICT in Vietnam under the legal name POSCO ICT Vietnam Co. Ltd.', '<p style="text-align: left;">\r\n	<span style="font-size:14px;color:#444;">After 3 years of operation in Vietnam in the form of a Representative Office, dated 10.01.2013, POSCO ICT representative office in HCM City officially became the first branch of POSCO ICT in Vietnam under the legal </span><span style="font-size: 14px;">name </span><span style="font-size: 14px;">POSCO ICT Vietnam </span><span style="font-size: 14px;">Co. Ltd</span><span style="font-size: 14px;">.</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/TLCT_1.JPG" style="width: 718px; height: 478px;" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/TLCT_2.JPG" style="width: 718px; height: 478px;" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/TLCT_3.JPG" style="width: 718px; height: 478px;" /></p>\r\n<p>\r\n	 </p>\r\n<p style="text-align: left;">\r\n	<span style="font-size:14px;color:#444;">On 01/10/2013, at company headquarters (Room 604A, Diamond Plaza, 34 Le Duan, District 1, HCMC) was held inauguration ceremony POSCO ICT Vietnam. The ceremony had the presence of many representatives of partner companies, organizations and business associations in Vietnam, South Korea, POSCO ICT and representatives from the parent company in South Korea to attend the ribbon cutting.</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size:14px;color:#444;">In particular, in his speech congratulated his South Korean Consulate General in HCMC. Ho Chi Minh City, next to the greetings and good wishes for the long term development of the company, also promises "Korean Consulate in Vietnam will support our best for sustainability and co-operation POSCO ICT Vietnam measures in Vietnam ".</span></p>\r\n', 'ckfinder/upload/userfiles/images/TLCT_3.JPG', '2014-11-25 10:15:10', 'Y', 'en', 'admin', '2015-10-16 00:00:00', 'admin', '2014-11-25 10:15:10'),
(21, 'NN', 5, 'News & Notice', 'Posco ICT Vietnam', 'First birthday of POSCO ICT Vietnam', 'POSCO ICT Vietnam Co., turns 1 year old on Oct 1st, 2014', '<p>\r\n	<span style="font-size:14px; color: #444;">On 01/10/2014: The company POSCO ICT Vietnam 1 year old.</span></p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/1NTL_7.JPG" style="width: 718px; height: 538px;" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size: 14px; color: #444;">His speech Kim Kwang Suk CEO report on business companies established after 1 year and business strategy in the years ahead.</span></p>\r\n<p>\r\n	 </p>\r\n<p style="text-align: justify;">\r\n	<img alt="" src="ckfinder/upload/userfiles/images/1NTL_5.JPG" style="width: 718px; height: 517px;" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size:14px; color: #444;">Cutting ceremony to celebrate the company turns 1 year old. Cake "Birthday" was cut by the CEO and representatives of some excellent staff from the department.</span></p>\r\n<p>\r\n	 </p>\r\n<p style="text-align: justify;">\r\n	<img alt="" src="ckfinder/upload/userfiles/images/1NTL_6.JPG" /></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size:14px; color: #444;">Memorial picture the entire staff at the 01 Anniversary of the establishment of the Company.</span></p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/1NTL_6.JPG" /></p>\r\n', 'ckfinder/upload/userfiles/images/1NTL_6.JPG', '2014-11-25 10:15:10', 'Y', 'en', 'admin', '2015-10-16 00:00:00', 'admin', '2014-11-25 10:15:10'),
(22, 'AT', 4, 'News & Notice', 'Posco ICT Vietnam', 'Social activities', 'Corporate culture of POSCO ICT Vietnam not only the business but also focused on volunteer activities to contribute to the social development of Vietnam.', '<p>\r\n	<span style="font-size: 14px; color: #444;">Corporate culture of POSCO ICT Vietnam not only the business but also focused on volunteer activities to contribute to the social development of Vietnam.</span></p>\r\n<p>\r\n	<span style="font-size: 14px; color: #444;"><strong>Volunteer activity of distributing meal in Tan Binh, HCM City</strong></span></p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/PCTS_1.JPG" style="width: 718px; height: 536px;" /></p>\r\n<p>\r\n	<span style="font-size: 14px; color: #444;">Distributing rice charity is periodic done twice a month by both the Korean and Vietnamese staff.</span></p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/PCTS_2.JPG" style="width: 718px; height: 477px;" /></p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/PCTS_3.JPG" style="width: 718px; height: 477px;" /></p>\r\n<p>\r\n	<span style="font-size: 14px; color: #444;">The smiling faces welcome tray of rice which is happiness of POSCO ICT Vietnam staff who joined this meaningful activity.</span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size: 14px; color: #444;"><strong>Giving books to children in Tan Binh, HCM City</strong></span></p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/PCTS_4.JPG" /></p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/PCTS_5.JPG" style="width: 718px; height: 404px;" /></p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/PCTS_6.JPG" style="width: 718px; height: 404px;" /></p>\r\n<p>\r\n	 </p>\r\n', 'ckfinder/upload/userfiles/images/PCTS_6.JPG', '2014-11-25 10:15:10', 'Y', 'en', 'admin', '2015-10-16 00:00:00', 'admin', '2014-11-25 10:15:10'),
(23, 'GP', 0, 'Global Posco', '극내와 해외 곳곳에서 고객 만족을 위해 항상 최선을 다하고 있습니다.    ', '', '', '<p style="text-align: left;">\r\n	<span style="font-size:14px;"><span style="color: rgb(68, 68, 68);">포스코 그룹은 철강, 신소재, 건설, 에너지, ICT 등 미래형 사업구조를 기반으로 <span style="color:#00457c;"><strong>Global Business Group </strong></span>으로 나아가고 있습니다.</span></span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/global_posco_kr.png" style="width: 719px; height: 374px;" /></p>\r\n', '', '2015-10-19 10:15:10', 'Y', 'kr', 'admin', '2015-10-19 10:15:10', 'admin', '2015-10-19 10:15:10'),
(24, 'GP', 0, 'Global Posco', 'POSCO ICT Vietnam always commit to satisfy customer on domestic and oversea.   ', '', '', '<p>\r\n	<span style="font-size:14px;"><span style="color:#444;">POSCO aims to become a</span> <span style="color:#00457c;"><strong>Global Business Group</strong></span> <span style="color:#444;">based on future-oriented businesses: steel, new materials, construction, energy, ICT, etc.</span></span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<img alt="" src="ckfinder/upload/userfiles/images/eng-S9.jpg" style="width: 718px;" /></p>\r\n', '', '2015-10-19 10:15:10', 'Y', 'en', 'admin', '2015-10-19 10:15:10', 'admin', '2015-10-19 10:15:10'),
(25, 'GP', 0, 'Global Posco', 'POSCO ICT Việt Nam luôn cam kết làm hài lòng khách hàng trong và ngoài nước. ', '', '', '<p>\r\n	<span style="font-size:14px;"><span style="color: rgb(68, 68, 68);">POSCO đặt mục tiêu trở thành <strong style="color:#00457c;font-size:16px;">Tập Đoàn Kinh Doanh Toàn Cầu</strong> dựa trên các định hướng kinh doanh tương lai: thép, vật liệu mới, xây dựng, năng lượng, công nghệ thông tin,...</span></span></p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="ckfinder/upload/userfiles/images/VN-S9.jpg" style="width: 717px; height: 429px; padding-top: 8px;" /></span></p>\r\n<p>\r\n	 </p>\r\n', '', '2015-10-19 10:15:10', 'Y', 'vn', 'admin', '2015-10-19 10:15:10', 'admin', '2015-10-19 10:15:10');
